import { useState, useEffect } from "react";
import Wrapper from "../styled-components/Wrapper";
import Hero from "../styled-components/Hero";
import Footer from "../styled-components/Footer";
import Language from "../styled-components/Language";
import HeroEN from "../styled-components/HeroEN";
import Form from "../styled-components/Form";
import Input from "../styled-components/Input";
import Button from "../styled-components/Button";

function Lalora() {
  const [language, setLanguage] = useState("español");
  const [formData, setFormData] = useState({
    nombre: "",
    correo: "",
  });
  const [errors, setErrors] = useState({
    nombre: "",
    correo: "",
  });

  useEffect(() => {
    (() => {
      if (localStorage.getItem("bajoNaranja")) {
        setLanguage(JSON.parse(localStorage.getItem("bajoNaranja")).language);
      }
    })();
  }, []);

  function languageChange() {
    if (language === "english") {
      localStorage.removeItem("bajoNaranja");
      return window.location.reload();
    }
    localStorage.setItem(
      "bajoNaranja",
      JSON.stringify({ language: "english" })
    );
    return window.location.reload();
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    let newErrors = { nombre: "", correo: "" };

    if (!formData.nombre.trim()) {
      newErrors.nombre = "Por favor, ingresa tu nombre";
    }

    if (!formData.correo.trim()) {
      newErrors.correo = "Por favor, ingresa tu correo electrónico";
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.correo)) {
      newErrors.correo = "Por favor, ingresa un correo electrónico válido";
    }

    setErrors(newErrors);

    if (Object.values(newErrors).every((val) => val === "")) {
      // Aquí puedes implementar la lógica para enviar el formulario
      alert("Formulario enviado correctamente");
    }
  };

  return (
    <Wrapper>
      <Language onClick={languageChange}>{language}</Language>
      <div className="wrapper--max">
        {language === "español" ? <Hero /> : <HeroEN />}
      </div>
      <div className="wrapper--max">
        {language === "español" ? (
          <>
            <Form onSubmit={handleSubmit}>
              <Input
                type="text"
                name="nombre"
                value={formData.nombre}
                placeholder="Nombre"
              />
              {errors.nombre && <span>{errors.nombre}</span>}
              <Input
                type="email"
                name="correo"
                value={formData.correo}
                placeholder="Correo electrónico"
              />
              {errors.correo && <span>{errors.correo}</span>}
              <Button type="submit">Solicitar Entrada</Button>
            </Form>
          </>
        ) : (
          <>
            <p>
              <Form onSubmit={handleSubmit}>
                <Input
                  type="text"
                  name="nombre"
                  value={formData.nombre}
                  placeholder="Name"
                />
                {errors.nombre && <span>{errors.nombre}</span>}
                <Input
                  type="email"
                  name="correo"
                  value={formData.correo}
                  placeholder="Email"
                />
                {errors.correo && <span>{errors.correo}</span>}
                <Button type="submit">Request Entry</Button>
              </Form>
            </p>
          </>
        )}
      </div>
      <Footer />
    </Wrapper>
  );
}

export default Lalora;
