import styled from "styled-components";

const ErrorMessage = styled.span`
  margin-top: 5px;
  margin-bottom: 20px;
  margin-left: 15px;
  font-family: 'AvenirBI';
  font-size: 20px;
  color: red;
`;

export default ErrorMessage;
