import { useState, useEffect } from "react";
import Wrapper from "../styled-components/Wrapper";
import Hero from "../styled-components/Hero";
import Footer from "../styled-components/Footer";
import Language from "../styled-components/Language";
import HeroEN from "../styled-components/HeroEN";
import Form from "../styled-components/Form";
import Input from "../styled-components/Input";
import Button from "../styled-components/Button";
import ErrorMessage from "../styled-components/ErrorMessage";
import { addUser } from "../api";

function Lalora() {
  const [language, setLanguage] = useState("español");
  const [formData, setFormData] = useState({
    nombre: "",
    correo: "",
  });
  const [errors, setErrors] = useState({
    nombre: "",
    correo: "",
  });
  const [errorMessages, setErrorMessages] = useState({
    español: {
      nombre: "Por favor, ingresa tu nombre",
      correo: "Por favor, ingresa un correo electrónico válido",
    },
    english: {
      nombre: "Please enter your name",
      correo: "Please enter a valid email",
    },
  });

  useEffect(() => {
    const storedLanguage = localStorage.getItem("bajoNaranja");
    if (storedLanguage) {
      setLanguage(JSON.parse(storedLanguage).language);
    }
  }, []);

  function languageChange() {
    if (language === "english") {
      localStorage.removeItem("bajoNaranja");
      return window.location.reload();
    }
    localStorage.setItem(
      "bajoNaranja",
      JSON.stringify({ language: "english" })
    );
    return window.location.reload();
  }

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    let newErrors = { nombre: "", correo: "" };

    if (!formData.nombre.trim()) {
      newErrors.nombre = errorMessages[language].nombre;
    }

    if (!formData.correo.trim()) {
      newErrors.correo = errorMessages[language].correo;
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.correo)) {
      newErrors.correo = errorMessages[language].correo;
    }

    setErrors(newErrors);

    if (Object.values(newErrors).every((val) => val === "")) {
      try {
        await addUser(formData, language); // Enviamos el idioma al backend
        alert(
          language === "español"
            ? "Usuario registrado correctamente"
            : "User registered successfully"
        );
      } catch (error) {
        alert(
          language === "español"
            ? "Error al registrar usuario"
            : "Error registering user"
        );
      }
    }
  };

  return (
    <Wrapper>
      <Language onClick={languageChange}>{language}</Language>
      <div className="wrapper--max">
        {language === "español" ? <Hero /> : <HeroEN />}
      </div>
      <div className="wrapper--max">
        {language === "español" ? (
          <>
            <Form onSubmit={handleSubmit}>
              <Input
                type="text"
                name="nombre"
                value={formData.nombre}
                placeholder="Nombre"
                onChange={handleChange}
              />
              <ErrorMessage>{errors.nombre}</ErrorMessage>
              <Input
                type="email"
                name="correo"
                value={formData.correo}
                placeholder="Correo electrónico"
                onChange={handleChange}
              />
              <ErrorMessage>{errors.correo}</ErrorMessage>
              <Button type="submit">Solicitar Entrada</Button>
            </Form>
          </>
        ) : (
          <>
            <p>
              <Form onSubmit={handleSubmit}>
                <Input
                  type="text"
                  name="nombre"
                  value={formData.nombre}
                  placeholder="Name"
                  onChange={handleChange}
                />
                <ErrorMessage>{errors.nombre}</ErrorMessage>
                <Input
                  type="email"
                  name="correo"
                  value={formData.correo}
                  placeholder="Email"
                  onChange={handleChange}
                />
                <ErrorMessage>{errors.correo}</ErrorMessage>
                <Button type="submit">Request Entry</Button>
              </Form>
            </p>
          </>
        )}
      </div>
      <Footer />
    </Wrapper>
  );
}

export default Lalora;
